mapper.select:
select
permname AS permname,
perm AS perm,
permcolumnid AS permcolumnid,
viewmode AS viewmode
from vman_perm
where 1 = 1

	and permname = #{permname}


	and perm = #{perm}


	and permcolumnid = #{permcolumnid}


	and viewmode = #{viewmode}

