mapper.select:
select
rolename AS rolename,
role AS role,
createrid AS createrid,
creatername AS creatername,
createdate AS createdate
from vman_role
where 1 = 1

	and rolename = #{rolename}


	and role = #{role}


	and createrid = #{createrid}


	and creatername = #{creatername}


	and createdate = #{createdate}

